mapper.insert:
insert into TB_SR_001MT (
	SR_TITLE, DUE_DT, SR_CONT, REL_SYS, SR_STAT, DELT_YN, FIRST_INPT_ID, FIRST_INPT_DT, LAST_INPT_ID, LAST_INPT_DT
)
values (
	#{srTitle}, #{dueDt, jdbcType=DATE}, #{srCont}, #{relSys}, 'REGI', 'N', #{firstInptId}, sysdate, #{lastInptId}, sysdate
)

mapper.select:

select
    rnum, SR_ID, SR_TITLE, REL_SYS, FIRST_INPT_ID, SR_STAT, REQ_DT, DUE_DT, MEM_NM, INST_ID, INST_NM
from (
    select
        SR_ID, SR_TITLE, REL_SYS, FIRST_INPT_ID, SR_STAT, REQ_DT, DUE_DT, MEM_NM, INST_ID, INST_NM,
        row_number() over(order by FIRST_INPT_DT desc) as rnum
    from (
        select
            SR_ID, SR_TITLE, REL_SYS, sr001mt.FIRST_INPT_ID, sr001mt.FIRST_INPT_DT, SR_STAT, REQ_DT, DUE_DT, 
            mb001mt.MEM_NM, mb001mt.INST_ID, mb001mt.INST_NM
        from TB_SR_001MT sr001mt
        join (
            select MEM_NO, MEM_NM, mbmt.INST_ID, ismt.INST_NM
            from TB_MB_001MT mbmt
            join TB_IS_001MT ismt on mbmt.INST_ID = ismt.INST_ID
        ) mb001mt on sr001mt.FIRST_INPT_ID = mb001mt.MEM_NO
        where sr001mt.DELT_YN = 'N'

            and FIRST_INPT_DT between #{search.startDate} and #{search.endDate}


            and REL_SYS = #{search.relSys}


            and SR_STAT = #{search.srStat}


            and SR_TITLE like '%' || #{search.keyword} || '%'

    )
)
where rnum between #{pager.startRowNo} and #{pager.endRowNo}

select count(*)
from TB_SR_001MT
where
	DELT_YN = 'N'

		and FIRST_INPT_DT between #{startDate} and #{endDate}


		and REL_SYS = #{relSys}


		and SR_STAT = #{srStat}


		and SR_TITLE like '%' || #{keyword}|| '%'


		and INST_ID = #{instId}
