mapper.select:
SELECT RNUM, APP_SR_ID, SR_ID, SR_TITLE, REL_SYS, MEM_NM, FIRST_INPT_ID, RCP_STAT, REQ_DT, DUE_DT, TASK_TYPE FROM ( SELECT A.APP_SR_ID, R.SR_ID, R.SR_TITLE, R.REL_SYS, M.MEM_NM, R.FIRST_INPT_ID, A.RCP_STAT, R.REQ_DT, R.DUE_DT, T.TASK_TYPE, ROW_NUMBER() OVER (ORDER BY R.FIRST_INPT_DT DESC) AS RNUM FROM TB_SR_002MT A JOIN TB_SR_001MT R ON A.SR_ID = R.SR_ID JOIN TB_MB_001MT M ON R.FIRST_INPT_ID = M.MEM_NO LEFT JOIN ( SELECT APP_SR_ID, TASK_TYPE FROM TB_SR_002NT WHERE (APP_SR_ID, PRG) IN ( SELECT APP_SR_ID, MAX(PRG) FROM TB_SR_002NT GROUP BY APP_SR_ID ) ) T ON A.APP_SR_ID = T.APP_SR_ID LEFT JOIN ( SELECT distinct
MEM_ID,
APP_SR_ID FROM TB_SR_001NT ) H ON A.APP_SR_ID = H.APP_SR_ID WHERE A.DELT_YN = 'N'
AND R.FIRST_INPT_DT BETWEEN #{searchDto.startDate} AND #{searchDto.endDate}
AND R.REL_SYS = #{searchDto.relSys}
AND A.WK_TYPE = #{searchDto.wkType}
AND (R.SR_TITLE LIKE '%' || #{searchDto.keyword} || '%' OR UPPER(R.SR_TITLE) LIKE UPPER('%' || #{searchDto.keyword} || '%'))
and A.APP_SR_ID like '%' || #{searchDto.keyword} || '%'
and M.MEM_NM like '%' || #{searchDto.keyword} || '%'
and A.RVW_CONT like '%' || #{searchDto.keyword} || '%'
AND T.TASK_TYPE = #{searchDto.tkType}
AND A.RCP_STAT = #{searchDto.rcpStat}
AND H.MEM_ID = #{searchDto.memId}
) WHERE RNUM BETWEEN #{pager.startRowNo} AND #{pager.endRowNo} ORDER BY RNUM ASC

SELECT COUNT(DISTINCT A.APP_SR_ID) FROM TB_SR_002MT A JOIN TB_SR_001MT R ON A.SR_ID = R.SR_ID LEFT JOIN TB_MB_001MT M ON R.FIRST_INPT_ID = M.MEM_NO LEFT JOIN ( SELECT APP_SR_ID, TASK_TYPE FROM TB_SR_002NT WHERE (APP_SR_ID, PRG) IN ( SELECT APP_SR_ID, MAX(PRG) FROM TB_SR_002NT GROUP BY APP_SR_ID ) ) T ON A.APP_SR_ID = T.APP_SR_ID LEFT JOIN ( SELECT DISTINCT
MEM_ID,
APP_SR_ID FROM TB_SR_001NT ) H ON A.APP_SR_ID = H.APP_SR_ID WHERE A.DELT_YN = 'N'
AND R.FIRST_INPT_DT BETWEEN #{startDate} AND #{endDate}
AND R.REL_SYS = #{relSys}
AND A.WK_TYPE = #{wkType}
AND (R.SR_TITLE LIKE '%' || #{keyword} || '%' OR UPPER(R.SR_TITLE) LIKE UPPER('%' || #{keyword} || '%'))
AND A.APP_SR_ID LIKE '%' || #{keyword} || '%'
AND M.MEM_NM LIKE '%' || #{keyword} || '%'
AND A.RVW_CONT LIKE '%' || #{keyword} || '%'
AND T.TASK_TYPE = #{tkType}
AND A.RCP_STAT = #{rcpStat}
AND H.MEM_ID = #{memId}

mapper.insert:
insert into TB_SR_002MT (
	SR_ID, FIRST_INPT_ID, FIRST_INPT_DT, LAST_INPT_ID, LAST_INPT_DT, DELT_YN
)	
values (
	#{srId}, #{lastInptId}, sysdate, #{lastInptId}, sysdate, 'N'
)

mapper.select:
SELECT MEM_ID, MEM_NO, MEM_NM, ROLE_1, ROLE_2, DEPT_ID FROM TB_MB_001MT WHERE ROLE_1 = 'ROLE_DEVE'
and DEPT_ID = #{deptId}
and MEM_NM LIKE '%' || #{memNm} || '%'

select APP_SR_ID
from TB_SR_002MT
where SR_ID = #{srId}
