mapper.select:
SELECT d.WK_DT, NVL(t.WK_HOUR, 0) AS WK_HOUR FROM (
SELECT #{date} AS WK_DT FROM DUAL
) d LEFT JOIN TB_SR_003NT t ON TRUNC(d.WK_DT) = TRUNC(t.WK_DT) AND t.MEM_ID = #{memId} AND t.APP_SR_ID = #{appSrId} LEFT JOIN ( SELECT APP_SR_ID FROM TB_SR_002NT WHERE NVL(PRG, 0) < 100 GROUP BY APP_SR_ID ) sr2 ON t.APP_SR_ID = sr2.APP_SR_ID

SELECT 1
FROM TB_SR_003NT
WHERE MEM_ID = #{memId}
  AND APP_SR_ID = #{appSrId}
  AND WK_DT = TRUNC(#{wkDt})

mapper.update:
UPDATE TB_SR_003NT
SET WK_HOUR = #{wkHour}
WHERE MEM_ID = #{memId}
  AND APP_SR_ID = #{appSrId}
  AND TRUNC(WK_DT) = TRUNC(#{wkDt})
